import postgres_for_tables as psql



def create_table(cursor):
    cursor.execute("""CREATE TABLE IF NOT EXISTS feedback_main (
        id SERIAL PRIMARY KEY, 
        name_en VARCHAR(255), 
        name_ru VARCHAR(255), 
        template_1 TEXT,
        template_2 TEXT,
        category VARCHAR(255),
        comment VARCHAR(255)
        );
        """)

